import React, { useState } from "react";

function TodoItem(props) {
  const [complete, setComplete] = useState(false);
  function removeTask(id) {
    // console.log(props.id);
    props.deleteItem(id);
  }
  function checkBoxHandler() {
    updateStatus(props.id, complete);
    return complete ? setComplete(false) : setComplete(true);
  }
  function updateStatus(id, newStatus) {
    props.updateStatus(id, newStatus);
  }
  return (
    <div>
      <li
        style={{
          textDecoration: complete ? "line-through" : null,
          listStyle: "none",
        }}
      >
        <input type="checkbox" value={props.status} onClick={checkBoxHandler} />
        {props.todo}
      </li>
      <button
        onClick={() => {
          removeTask(props.id);
        }}
      >
        Delete
      </button>
    </div>
  );
}

export default TodoItem;
